import {FaArrowCircleRight , FaArrowCircleLeft } from 'react-icons/fa';

const Modal = ({
    clickedImg,
    setClickedImg,
    handelRotationRight,
    handelRotationLeft
  }) => {
    const handleClick = (e) => {
      if (e.target.classList.contains("dismiss")) {
        setClickedImg(null);
      }
    };
  
    return (
      <>
        <div className="overlay h-100 bg-dark bg-opacity-50 fixed-top d-flex align-items-center dismiss" onClick={handleClick}>
          <img className='img-fluid mx-auto w-50 h-50' src={clickedImg} alt="bigger pic" />
          <span className="pe-auto text-white position-fixed top-0 end-0 d-flex bg-transparent dismiss" onClick={handleClick}>
            <i class="fa fa-close dismiss"></i>
          </span>
          <div onClick={handelRotationLeft} className="overlay-arrows_left">
            <div><FaArrowCircleLeft className='light-left-arrow position-fixed start-0 top-50 px-1 text-white'/></div>
          </div>
          <div onClick={handelRotationRight} className="overlay-arrows_right">
            <div><FaArrowCircleRight className='light-right-arrow position-fixed end-0 end-0'/></div>
          </div>
        </div>
      </>
    );
  };
  
  export default Modal;
