import React, { useEffect, useState } from 'react';
import GalleryData from './GalleryData';
import Modal from './Modal';


function NFT_Page(){
    
    const [tag , setTag] = useState('war');
    const [filteredImages, setFilteredImages] = useState([]);

    useEffect(function(){
        tag === 'all' ? setFilteredImages(GalleryData) : setFilteredImages(GalleryData.filter( Gdata => Gdata.tag === tag));
    }, [tag])
    
    function TagButton({name , handleSetTag , classprop}){
        return <button className={classprop ? 'active_tag' : null} onClick={function(){handleSetTag(name)} }>{name.toUpperCase()}</button>
    }

    const handleClick = (images , index) => {
        setTag(index);
        setFilteredImages(images.link);

    }
    
    const handleRotationRight = () => {
        const totallength = GalleryData.data.length;
        if(filteredImages +1 >= totallength){
            setFilteredImages(0);
            const newUrl = GalleryData.data[0].link;
            setTag(newUrl);
            return;
        }
        const newIndex= tag + 1;
        const newUrl = GalleryData.data.filter((item) => {
            return GalleryData.data.indexOf(item) === newIndex;
        });
        const newItem = newUrl[0].link;
        setTag(newItem);
    }

    return(
        <>
        <div className='nft'>
            <div className='tags'>        
                <TagButton classprop={tag ==='all' ? true : false} onClick={TagButton} name='all' handleSetTag={setTag} />
                <TagButton classprop={tag ==='angel' ? true : false} onClick={TagButton} name='angel' handleSetTag={setTag} />
                <TagButton classprop={tag ==='devil' ? true : false} onClick={TagButton} name='devil' handleSetTag={setTag} />
                <TagButton classprop={tag ==='war' ? true : false} onClick={TagButton} name='war' handleSetTag={setTag} />
            </div>

            <div className='container'>
                {filteredImages.map(function(images , index){
                    return(
                        <div className='image-card' key={index}>
                            <img  src={images.imageName} onClick={() => handleClick(images , index)}/>
                        </div>
                        )
                })}
            </div>
            {tag && <Modal clickedimage={tag}/>}
        </div>
        </>
    )
}

export default NFT_Page;
