import React , {useState} from 'react';
import GalleryData from './GalleryData';
import './index.css';


function FilterGallery(){

    const [items , setitems] = useState(GalleryData);
    const [branch , setbranch] = useState(items);

    function filterfu(categet){
        
       const updatedcat = GalleryData.filter(function(parser){
            return parser.category === categet;
        });
        console.log(updatedcat);
        setitems(updatedcat);
    }
    console.log('clicked');
    console.log(items);
    function SfilterF(asadP){
        const againfilter = items.filter(function(newparser){
            return newparser.tag === asadP;
        });
        console.log(againfilter);
        setbranch(againfilter);
        
    }

    const checkasad = 'assad';
    
    const itemstyle = {};
    const branchstyle = {};

    function Checking(){
        if(checkasad === 'asad'){
            // itemstyle.display = 'flex';
            // branchstyle.display = 'none';
           return <button>asad</button>
        }else{
            // itemstyle.display = 'none';
            // branchstyle.display = 'flex';
            return <button>ali</button>
        }
    }


    return(
        <>
            <h1 className='mt-5 text-white text-center main-heading'>Select your favorite Picture</h1>
            <hr className='text-white'/>
            <div className='menu-tabs container'>
                <div className='menu-tab d-flex d-grid gap-3 justify-content-between'>
                    <div className='menu-tab d-flex d-grid gap-3 '>
                        <button className='btn btn-warning' onClick={() => setitems(GalleryData)}>All</button>
                        <button  className='btn btn-warning' onClick={() => filterfu('breakfast')}>Breakfast</button>
                        <button className='btn btn-warning' onClick={() => filterfu('evening')}>evening</button>
                        <button className='btn btn-warning' onClick={() => filterfu('dinner')}>dinner</button>
                        <button className='btn btn-warning' onClick={() => filterfu('lunch')}>lunch</button>
                        <Checking className='btn btn-warning' />
                    </div>

                    <div className='menu-tab d-flex d-grid gap-3'>
                        <button className='btn btn-warning' onClick={() => setitems(GalleryData)}>All</button>
                        <button  className='btn btn-warning' onClick={() => SfilterF('angel')}>Angel</button>
                        <button className='btn btn-warning' onClick={() => SfilterF('devil')}>Devil</button>
                        <button className='btn btn-warning' onClick={() => SfilterF('war')}>War</button>
                        <button className='btn btn-warning' onClick={() => SfilterF('weapon')}>Weapon</button> 
                    </div>
                </div>
            </div>

            {/* Main Section */}
                    <div  className='container filter-gallery'>
                        <div style={itemstyle} className='row gx-4 gy-4'>
                            {
                                items.map(function(parameter){
                                    return (
                                        <>
                                        <div className='Col-lg-4 col-md-4  col-10 col-xxl-4 mx-auto'>
                                                <h3 className='text-white'>{parameter.id}</h3>
                                                <h4 className='text-white'>{parameter.category}</h4>
                                                <h4 className='text-white'>{parameter.tag}</h4>
                                                <img src={parameter.imageName} className='img-fluid' />
                                                
                                        </div>
                                        </>
                                    )
                                })                        
                            }
                        </div>
                        <div style={branchstyle} className='row gx-4 gy-4'>
                            {/* {
                                branch.map(function(parameter){
                                    return (
                                        <>
                                        <div className='Col-lg-4 col-md-4  col-10 col-xxl-4 mx-auto'>
                                                
                                                <h4 className='text-white'>{parameter.category}</h4>
                                                <h4 className='text-white'>{parameter.tag}</h4>
                                                <img src={parameter.imageName} className='img-fluid' />
                                                
                                        </div>
                                        </>
                                    )
                                })                        
                            } */}
                        </div>
                    </div>
                    
                        
               
         
        </>
    )
}

export default FilterGallery;
