import React, { useEffect, useState } from 'react';
import GalleryData from './GalleryData';


function NFT_Page(){
    
    const [tag , setTag] = useState('war');
    const [filteredImages, setFilteredImages] = useState([]);

    useEffect(function(){
        tag === 'all' ? setFilteredImages(GalleryData) : setFilteredImages(GalleryData.filter( Gdata => Gdata.tag === tag));
    }, [tag])
    
    function TagButton({name , handleSetTag}){
        return <button onClick={function(){handleSetTag(name)} }>{name}</button>
    }

    return(
        <>
        <div className='nft'>
            <div className="tags">        
                <TagButton onClick={TagButton} name='all' handleSetTag={setTag} />
                <TagButton onClick={TagButton} name='angel' handleSetTag={setTag} />
                <TagButton onClick={TagButton} name='devil' handleSetTag={setTag} />
                <TagButton onClick={TagButton} name='war' handleSetTag={setTag} />
            </div>
            
            <div className='container'>
                {filteredImages.map(function(images , index){
                    return(
                        <div className='image-card' key={index}>
                            <img  src={images.imageName}/>
                        </div>
                        )
                })}
            </div>
        </div>
        </>
    )
}

export default NFT_Page;
