import React , {useState} from 'react';
import GalleryData from './GalleryData';
import './index.css';


function FilterGallery(){

    const [itemsFilter, setItemsFilter] = useState("")
    const [branchFilter, setBranchFilter] = useState("")
 
    const filterData = () =>{
        if(itemsFilter !== ""){
            const anyvariable = GalleryData.filter(item => item.category === itemsFilter)
            if(branchFilter !== ''){
                return anyvariable.filter(item => item.tag === branchFilter) 
            }else{
                return anyvariable;
            }
        }else{
            return GalleryData;
        }

    }



    return(
        <>
            <h1 className='mt-5 text-white text-center main-heading'>Select your favorite Picture</h1>
            <hr className='text-white'/>
            <div className='menu-tabs container'>
                <div className='menu-tab helloworld d-flex d-grid gap-3 justify-content-between'>
                    <div className='menu-tab d-flex d-grid gap-3 '>
                        <button className={`btn ${itemsFilter === '' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setItemsFilter("")}>All</button>
                        <button  className={`btn ${itemsFilter === 'breakfast' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setItemsFilter('breakfast')}>Breakfast</button>
                        <button className={`btn ${itemsFilter === 'evening' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setItemsFilter('evening')}>evening</button>
                        <button className={`btn ${itemsFilter === 'dinner' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setItemsFilter('dinner')}>dinner</button>
                        <button className={`btn ${itemsFilter === 'lunch' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setItemsFilter('lunch')}>lunch</button>

                    </div>

                    <div className='menu-tab d-flex d-grid gap-3'>
                        {/* <button className={`btn ${branchFilter === '' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setBranchFilter("")}>All</button> */}
                        <button  className={`btn ${branchFilter === 'angel' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setBranchFilter('angel')}>Angel</button>
                        <button className={`btn ${branchFilter === 'devil' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setBranchFilter('devil')}>Devil</button>
                        <button className={`btn ${branchFilter === 'war' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setBranchFilter('war')}>War</button>
                        <button className={`btn ${branchFilter === 'weapon' ? 'btn-warning' : 'btn-outline-warning'}`} onClick={() => setBranchFilter('weapon')}>Weapon</button> 
                    </div>
                </div>
            </div>

            {/* Main Section */}
                    <div  className='container filter-gallery'>
                        <div className='row gx-4 gy-4'>
                            {
                                filterData().map((parameter) => 
                                     (
                                        
                                        <div className='Col-lg-4 col-md-4  col-10 col-xxl-4 mx-auto'>
                                                
                                            <h3 className='text-white'>{parameter.id}</h3>
                                            <h4 className='text-white'>{parameter.category}</h4>
                                            <h4 className='text-white'>{parameter.tag}</h4>
                                            <img src={parameter.imageName} className='img-fluid' />
                                                
                                        </div>
                                        
                                    )
                                )                        
                            }
                        </div>

                    </div>
                    
                        
               
         
        </>
    )
}

export default FilterGallery;
