import React, { useState } from 'react';
import GalleryData from './GalleryData';
import image1 from './images/image1.jpg'
import image4 from './images/image4.jpg'
import Devil from './Devil';
import Angel from './Angel';

function Store(){

const [angelNft , SetAngelNft] = useState(false);
const [DevilNft , SetDevilNft] = useState(false);
const [hideDiv , SetHideDiv] = useState(false);

    function GetAngel(){
        SetAngelNft(true);
        SetDevilNft(false);
    }


    function GetDEvil(){
        SetDevilNft(true);
        SetAngelNft(false);
    }



    return(
        <>
        {hideDiv &&         
            <div className='d-flex store-container mt-5  gap-5 container'>
                    <div>   
                        <img onClick={GetAngel} className='img-fluid' src={image1} />
                        <h4 class=" bg-danger  p-2 text-white">Angel NFT Store</h4>
                    </div>
                <div>
                        <img onClick={GetDEvil} className='bg-transparent img-fluid' src={image4} />
                        <h4 class=" bg-danger  p-2 text-white">Devil NFT Store</h4>
                </div>
            </div>
        }
        {!hideDiv && 
            <div className='button-container  d-flex  my-4 p-2'>
                <button className='mb-5 mt-5 ms-5 text-white border-white btn-outline-danger btn fs-4 w-25 p-4'>
                    Go To Store Again
                </button>
            </div>
        }


        <div className='button-container  d-flex justify-content-center  my-4 p-2'>
                <button className= 'mb-5 mt-5 text-white border-white btn-outline-danger btn fs-4 w-25 mx-auto p-4 ' >Get Weapon NFT</button>
                <button className= 'mb-5 mt-5 text-white  border-white btn-outline-danger btn fs-4 w-25 mx-auto p-4 ' >Get Armour NFT</button>
        </div>

        {angelNft && <Angel/>}
        {DevilNft && <Devil/>}



        </>
    )
}

export default Store;


