import React , { useState } from 'react';
import ToDoLists from './ToDoLists';

function App(){
  
  const [inputList , setInputList] = useState();
  const [stateButton , setStateButton] = useState([]); 
  // yahan hum ne stateButton k ander 1 khali array save ker dia he,

  function onChangeGet(event){
    setInputList(event.target.value)
    // jese hi koi inputfield me value likhe ga , wo inputList k ander store ho jae gi, 
  }

  function onClickGet(event){
    setStateButton(function holdarray(old){
      return [...old , inputList]
    })

    setInputList(''); 
    // yahan mene setInputList ki value ko empty ker dia he, k jese hi button per click kia jae input field ki value empty ho jae, lekin yad rhe is k lie inputfield k value attribute mein inputList hona  chaie,  ye instruction uper wale function k run hone k bad chale gi, or uper wala function kese run ho rha he ye neeche parh lein, 
  }
  // onClickGet k ander hum ne setStateButton mein 1 updated array store kerwaya, setStateButton bhi ap ki useState k khali array ko access ker skta he, is lie hum ne is k ander function likha or use old ka parameter dia or us parameter ki madad se three dot ko istimal kerte hue mojuda array ko fetch kerwa lia ,  or user jo new value de rha he inputlist k ander use bhi fetch kerwa lia, yani us array me jo values pehle se mojud then wo bhi return hon or jo user new type ker rha he wo bhi return hon, 

  // ab simple si bat he k setStateButton ap k useState me mojud khali array ko ya us me jo array pehle se mojud he use access ker skta he , to hum ne setStateButton k ander pehle se mojud array or new likhi jane value ko return kerwa lia he, ab ap jante hein k setStateButton jo bhi return kerwa rha he wo stateButton k ander store ho ga, yani array ki her value stateButton k ander store ho gi,
  // jise hum aage ol tag k ander map function likh ker li tag k ander her value ko alag alag store kerwaen ge,

  function deleteitems(id){
    setStateButton(function oldfilter(old){
      return old.filter(function gofilter(arrelem, index){
        return index !==arrelem.target.id;
      })
    })
  }

  return(
    <React.Fragment>
      <div className='main_div'>
        <div className='center_div'>
          <br />
          <h1> ToDo List</h1>
          <input type='text' placeholder='Add a Items' value={inputList} onChange={onChangeGet} /> 
          <button onClick={onClickGet}> + </button>
          
          <ol>
            {stateButton.map(function mapFunction(listvalues , index){
            
              return <ToDoLists 
                  key={index}
                  id={index}
                  text={listvalues}
                  onSelection={deleteitems}

              />

            })}
          
            {/* Line 6 mein mene Button k lie use State mein 1 khali array banaya he, wo khali array stateButton me store he, to mein stateButton k sath map function istimal ker rha hu, map function mein mene 1 function likh dia he or use 1 parameter de dia he, or is function mein mene li k ander wo parameter return kerwa lia he, yAni Map ka ye parameter StateButton k ander mojud array ko li k ander fetch kare ga, is ka sirf itna hi kaam he, ye array me mojud her value ko 1 new li me store kare ga, */}
          </ol>
        </div>
      </div>
    </React.Fragment>
    
  )
}

export default App;
