import React, {useState} from 'react';
import Clock from '@viviedu/react-clock';
import './index.css'

function App() {
  const [value, setValue] = useState(new Date());

  function useEffect() {
    const interval = setInterval(
      () => setValue(new Date()),
      1000
    );

    return function () {
      clearInterval(interval);
    }
  } [];

  return (
    <div>
      <p>Current time:</p>
      <Clock value={value} />
    </div>
  )
}

export default App;
