import React , { useState } from 'react';

function App(){

  const [fullname, setName] = useState({
    fname: '',
    lname: ''
  })
  
  function onChangeGet(e){
    const attributeName = e.target.name;
    const variableValue = e.target.value;

    setName(function(PreValue){
      if (attributeName === 'firstinput'){
        return {
          fname: variableValue ,
          lname: PreValue.lname,
        }
      }else if(attributeName === 'lastinput'){
        return {
          fname: PreValue.fname ,
          lname: variableValue,
        }
      }
    })

  }


  function onSubmitsFunction(event){
    event.preventDefault();
    alert('SUbimitted Successfully');
  }

  return(
    <>
    <form onSubmit={onSubmitsFunction}>
      <div>
        <h1> Hello {fullname.fname}  {fullname.lname}</h1>
        <input type='text' placeholder="Enter Your Name" name='firstinput'  onChange={onChangeGet}/>
        <input type='text' placeholder="Enter Your Name" name='lastinput'  onChange={onChangeGet}/>
        <button>Click Me üëç</button>
      </div>
    </form>
    </>
    
  )
}

export default App;
