import React , {useState} from 'react';
import GalleryData from './GalleryData';



function FilterGallery(){

    const [items , setitems] = useState(GalleryData);
    const [branch , setbranch] = useState(GalleryData);

    function filterfu(categet){
       const updatedcat = GalleryData.filter(function(parser){
            return (parser.category === categet)
        });
        setitems(updatedcat);
    }

    function branchfunc(branchget){
        const branchdisplay = GalleryData.filter(function(branchparser){
             return (branchparser.tag === branchget)
         });
         setbranch(branchdisplay);
    }

    return(
        <>
            <h1 className='mt-5 text-white text-center main-heading'>Select your favorite Picture</h1>
            <hr className='text-white'/>
            <div className='menu-tabs container'>
                <div className='menu-tab d-flex d-grid gap-3 justify-content-between'>
                    <div className='menu-tab d-flex d-grid gap-3 '>
                        <button className='btn btn-warning' onClick={() => setitems(GalleryData)}>All</button>
                        <button className='btn btn-warning' onClick={() => filterfu('breakfast')}>Breakfast</button>
                        <button className='btn btn-warning' onClick={() => filterfu('evening')}>evening</button>
                        <button className='btn btn-warning' onClick={() => filterfu('dinner')}>dinner</button>
                        <button className='btn btn-warning' onClick={() => filterfu('lunch')}>lunch</button>
                    </div>

                    <div className='menu-tab d-flex d-grid gap-3'>
                        <button className='btn btn-warning' onClick={() => setbranch(GalleryData)}>All</button>
                        <button className='btn btn-warning' onClick={() => branchfunc('angel')}>Angel</button>
                        <button className='btn btn-warning' onClick={() => branchfunc('devil')}>Devil</button>
                        <button className='btn btn-warning' onClick={() => branchfunc('war')}>War</button>
                        <button className='btn btn-warning' onClick={() => branchfunc('weapon')}>Weapon</button> 
                    </div>
                </div>
            </div>

            {/* Main Section */}
            <div className='menu-items mb-5 container-fluid mt-5'>
                <div className='row space text-white'>
                    <div className='col-11 mx-auto'>
                        <div className='item1 col-12 col-lg-4'>
                            {
                                [items , branch].map(function(parameter){
                                    return (
                                        <>
                                        <div className='row mt-5 mb-5 bg-danger Item-inside'>
                                        {/* Images */}
                                            <div className='bg-danger  p-0 col-12 col-md-12 col-lg-4'>
                                                <img src={parameter.imageName} className='img-fluid bg-dagner' />
                                            </div>
                                        {/* Description */}
                                            <div className='bg-danger col-12 col-md-8 col-lg-8'>
                                                <div className='bg-danger main-title pt-4 pb-3'>
                                                    <h6 className='bg-danger'>{parameter.id}</h6>
                                                    <h1 className='bg-danger'>{parameter.name}</h1>
                                                    <h6 className='bg-danger'>{parameter.category}</h6>
                                                    <p className='bg-danger'>{parameter.description}</p>
                                                    <p className='bg-success'>Branch : {parameter.tag}</p>
                                                </div>
                                                <div className='bg-danger menu-price-book'>
                                                    <h4 className='bg-danger'>{parameter.price}</h4>
                                                    <a href="#">
                                                        <button className='btn btn-primary'>Buy it</button>
                                                    </a>
                                                </div>
                                            </div>
                                        </div>
                                        </>
                                    )
                                })                        
                            }

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default FilterGallery;
