import React , {useState} from 'react';
import GalleryData from './GalleryData';
import './index.css';


function Devil(){

    const [itemsFilter, setItemsFilter] = useState("")
    const [branchFilter, setBranchFilter] = useState("")
 
    const filterData = () =>{
        if(itemsFilter !== ""){
            const anyvariable = GalleryData.filter(item => item.category === itemsFilter)
            if(branchFilter !== ''){
                return anyvariable.filter(item => item.tag === branchFilter) 
            }else{
                return anyvariable;
            }
        }else{
            return GalleryData;
        }

    }

    return(
        <>
            <h3 className='container mt-5 text-white main-heading'>Select Your Favorite Devil</h3>
            <hr className='my-5 text-white'/>
            <div className='menu-tabs container'>
                <div className='filter-buttons helloworld d-flex d-grid gap-3 justify-content-between'>
                    
                    <div className='filter-buttons d-flex d-grid gap-3 '>
                        
                        <button className={`btn text-white ${itemsFilter === '' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setItemsFilter("")}>All</button>
                        <button className={`btn text-white ${itemsFilter === 'breakfast' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setItemsFilter('breakfast')}>Asad</button>
                        <button className={`btn text-white ${itemsFilter === 'evening' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setItemsFilter('evening')}>Khan</button>
                        <button className={`btn text-white ${itemsFilter === 'dinner' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setItemsFilter('dinner')}>Ali</button>
                        <button className={`btn text-white ${itemsFilter === 'lunch' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setItemsFilter('lunch')}>Amir</button>

                    </div>

                    <div className='filter-buttons d-flex d-grid gap-3'>
                        
                        <button className={`btn text-white ${branchFilter === 'angel' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setBranchFilter('angel')}>Joiya</button>
                        <button className={`btn text-white ${branchFilter === 'devil' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setBranchFilter('devil')}>Khan</button>
                        <button className={`btn text-white ${branchFilter === 'war' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setBranchFilter('war')}>Rajput</button>
                        <button className={`btn text-white ${branchFilter === 'weapon' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setBranchFilter('weapon')}>Mughal</button> 
                        <button className={`btn text-white ${branchFilter === '' ? 'btn-danger' : 'btn-outline-danger'}`} onClick={() => setBranchFilter("")}>Clear</button>
                    </div>

                    <div className='filter-buttons d-flex d-grid gap-3'>
                        <select className='btn btn-outline-danger text-white'>
                            <option className='bg-danger text-white'>Option 1</option>
                            <option className='bg-danger text-white'>Option 2</option>
                            <option className='bg-danger text-white'>Option 3</option>
                            <option className='bg-danger text-white ' >Option 4</option>
                            <option className='bg-danger text-white ' >Option 5</option>
                        </select>
                    </div>

                </div>
            </div>

            {/* Main Section */}
                    <div  className='container filter-gallery'>
                        <div className='row gx-4 gy-4'>
                            {
                                filterData().map((parameter) => 
                                     (
                                        
                                        <div className='Col-lg-4 col-md-4  col-10 col-xxl-4 mx-auto'>   
                                            <img src={parameter.imageName} className='img-fluid' />            
                                        </div>
                                        
                                    )
                                )                        
                            }
                        </div>

                    </div>
                    
                        
               
         
        </>
    )
}

export default Devil;
