import React, { useState , useEffect } from "react";
import Modal from "./Modal";
import GalleryData from "./GalleryData";

function App() {
  const [clickedImg, setClickedImg] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(null);

  const [tag , setTag] = useState('war');
  const [filteredImages, setFilteredImages] = useState([]);

  useEffect(function(){
    tag === 'all' ? setFilteredImages(GalleryData) : setFilteredImages(GalleryData.filter( Gdata => Gdata.tag === tag));
  }, [tag])

  function TagButton({name , handleSetTag , classprop}){
      return <button className={classprop ? 'active_tag' : null} onClick={function(){handleSetTag(name)} }>{name.toUpperCase()}</button>
  }

  const handleClick = (item, index) => {
    setCurrentIndex(index);
    setClickedImg(item.imageName);
  };

  const handelRotationRight = () => {
    const totalLength = GalleryData.GalleryData.length;
    if (currentIndex + 1 >= totalLength) {
      setCurrentIndex(0);
      const newUrl = GalleryData.GalleryData[0].imageName;
      setClickedImg(newUrl);
      return;
    }
    const newIndex = currentIndex + 1;
    const newUrl = GalleryData.GalleryData.filter((item) => {
      return GalleryData.indexOf(item) === newIndex;
    });
    const newItem = newUrl[0].imageName;
    setClickedImg(newItem);
    setCurrentIndex(newIndex);
  };

  const handelRotationLeft = () => {
    const totalLength = GalleryData.length;
    if (currentIndex === 0) {
      setCurrentIndex(totalLength - 1);
      const newUrl = GalleryData[totalLength - 1].imageName;
      setClickedImg(newUrl);
      return;
    }
    const newIndex = currentIndex - 1;
    const newUrl = GalleryData.filter((item) => {
      return GalleryData.indexOf(item) === newIndex;
    });
    const newItem = newUrl[0].imageName;
    setClickedImg(newItem);
    setCurrentIndex(newIndex);
  };

  return (
    <>
    <div className="wrapper">
      {GalleryData.map((item, index) => (
        <div key={index} className="wrapper-images">
          <img
            src={item.imageName}
            alt={item.text}
            onClick={() => handleClick(item, index)}
          />
        </div>
      ))}
      <div>
        {clickedImg && (
          <Modal
            clickedImg={clickedImg}
            handelRotationRight={handelRotationRight}
            setClickedImg={setClickedImg}
            handelRotationLeft={handelRotationLeft}
          />
        )}
      </div>
    </div>
    
    <div className='container'>
      {filteredImages.map(function(images , index){
          return(
              <div className='image-card' key={index}>
                  <img  src={images.imageName} />
              </div>
              )
      })}
    </div>
    </>
    
  );
}

export default App;
