import React , { useState } from 'react';

function App(){

  const MyColor = "blue";
  const sname = "Asad Khan";

  const [scolor, setcolor] = useState(MyColor);
  const [cname, setname] = useState(sname);

  function Mouseclick(){
    let updatecolor = "Yellow";
    

    
    setcolor(updatecolor);
  }

  function MouseDouble(){
    let newname = "Hello World";
    setname(newname);
  }
  return(
    <>
    <div style={ { backgroundColor : scolor } }>
      <button onClick={Mouseclick} onDoubleClick={MouseDouble}>{cname}</button>
    </div>
    </>
  )
}

export default App;
