import React , { useState } from 'react';

function App(){
  
  const [incre , setincre] = useState(0);

  function IncrementF(){
    setincre(incre + 1)
  }

  function DecrementF(){
    let limitdecre = incre;
    if (limitdecre > 0) {
      setincre(incre - 1)
    }else{
      alert("0 limit cross")
    }
  }

  return(
    <React.Fragment>
      <h1>{incre}</h1>
      <button onClick={IncrementF}><i class="fa fa-plus" aria-hidden="true" style={{color:'black', backgroundColor:'transparent'}}></i></button>
      <button onClick={DecrementF}>Decrement</button>
      <i class="fa-solid fa-user"></i>
    </React.Fragment>
    
  )
}

export default App;
