import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import GalleryData from './GalleryData';
import image1 from './images/image1.jpg'
import image2 from './images/image2.jpg'
import image3 from './images/image3.jpg'
import image4 from './images/image4.jpg'
import image5 from './images/image5.jpg'
import image6 from './images/image6.jpg'
import image7 from './images/image7.jpg'
import image8 from './images/image8.jpg'
import image9 from './images/image9.jpg'
import image10 from './images/image10.jpg'

function NFT_Page(){
    
    const [tag , setTag] = useState('war');
    const [filteredImages, setFilteredImages] = useState([]);

    useEffect(function(){
        tag === 'all' ? setFilteredImages(GalleryData) : setFilteredImages(GalleryData.filter( Gdata => Gdata.tag === tag));
    }, [tag])
    
    function TagButton({name , handleSetTag , classprop}){
        return <button className={classprop ? 'active_tag' : null} onClick={function(){handleSetTag(name)} }>{name.toUpperCase()}</button>
    }


    return(
        <>
        <div className='nft'>
            <div className='tags'>        
                <TagButton classprop={tag ==='all' ? true : false} onClick={TagButton} name='all' handleSetTag={setTag} />
                <TagButton classprop={tag ==='angel' ? true : false} onClick={TagButton} name='angel' handleSetTag={setTag} />
                <TagButton classprop={tag ==='devil' ? true : false} onClick={TagButton} name='devil' handleSetTag={setTag} />
                <TagButton classprop={tag ==='war' ? true : false} onClick={TagButton} name='war' handleSetTag={setTag} />
            </div>

            <div className='container'>
                {filteredImages.map(function(images , index){
                    return(
                        <div className='image-card' key={index}>
                            <img onClick={lightboxtriger} src={images.imageName} />
                        </div>
                        )
                })}
            </div>

        </div>

<button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#gallery-popup">
  Launch demo modal
</button>


<div class="modal fade" id="gallery-popup" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        {/* <h5 class="modal-title" id="exampleModalLabel">Modal title</h5> */}
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <img src={ image8} class="modal-img"/>
      </div>

    </div>
  </div>
</div>


        </>
    )
}

export default NFT_Page;
