import React , { useState } from 'react';

function App(){

  const [fullname, setName] = useState({
    fname: '',
    lname: '',
    email: '',
    phone: ''
  })
  
  function onChangeGet(e){
    const {name , value} = e.target;
    setName(function(PreValue){    
      return{
        ...PreValue,
        [name] : value,
      };   
    })
  }


  function onSubmitsFunction(event){
    event.preventDefault();
    alert('SUbimitted Successfully');
  }

  return(
    <>
    <form onSubmit={onSubmitsFunction}>
      <div>
        <h1> Hello {fullname.fname}  {fullname.lname}</h1>
        <h2> {fullname.email}</h2>
        <h3> {fullname.phone}</h3>
        <input type='text' placeholder="Enter Your Name" name='firstinput'   value={fullname.fname} onChange={onChangeGet}/>
        <input type='text' placeholder="Enter Your Name" name='lastinput'    value={fullname.lname} onChange={onChangeGet}/>
        <input type='email' placeholder="Enter Your email" name='emailinput' value={fullname.email} onChange={onChangeGet}/>
        <input type='number' placeholder="Enter Your phone" name='phoneinput' value={fullname.phone} onChange={onChangeGet}/>
        <button>Click Me üëç</button>
      </div>
    </form>
    </>
    
  )
}

export default App;
