import React, { useEffect, useState } from 'react';
import GalleryData from './GalleryData';


function NFT_Page(){
    
    const [tag , setTag] = useState('war');
    const [filteredImages, setFilteredImages] = useState([]);

    useEffect(function(){
        tag === 'all' ? setFilteredImages(GalleryData) : setFilteredImages(GalleryData.filter( Gdata => Gdata.tag === tag));
    }, [tag])
    
    function TagButton(e){
        setTag(e.target.value)
    }

    return(
        <>
        <div className='nft'>
            <button onClick={TagButton} >all</button>
            <button onClick={TagButton} >angel</button>
            <button onClick={TagButton} >devil</button>
            <button onClick={TagButton} >war</button>
            {filteredImages.map(function(images){return(<div>{images.imageName}</div>)})}
        </div>
        </>
    )
}

export default NFT_Page;
